INSERT INTO `peca`.`cinema_projection` (`name`, `actors`,`geners`, `director`,`runtime`, `poster`, `rating`,`storyline`,`crepertoar`) VALUES ('Kad porastem bicu kengur', 'N.Glogovac,S.Trifunovic','komedija','R.Andric','120',"https://upload.wikimedia.org/wikipedia/sr/d/d3/Kad_porastem_bicu_kengur.jpg", '9.9',"Citava radnja filma odigrava se kroz tri price u beogradskoj opštini Vozdovac. Prva prica govori o mladom studentu filmske montaze,Braci..",'1');
INSERT INTO `peca`.`cinema_projection` (`name`, `actors`,`geners`, `director`,`runtime`, `poster`, `rating`,`storyline`,`crepertoar`) VALUES ('Maratonci trce pocasni krug', 'P.Vuisic,D.Stojkovic,M.Aleksic','komedija','S.Sijan','130',"https://s1-ssl.dmcdn.net/fJSRX/1280x720-iuf.jpg", '9.9',"Radnja je smeštena 1935. godine, posle atentata na kralja Aleksandra u Marseju, u maloj srpskoj palanci. Porodica Topalovic ima pet generacija muskaraca..",'1');
INSERT INTO `peca`.`cinema_projection` (`name`, `actors`,`geners`, `director`,`runtime`, `poster`, `rating`,`storyline`,`crepertoar`) VALUES ('Ko to tamo peva', 'P.Vuisic,D.Stojkovic','action','S.Sijan','130',"https://upload.wikimedia.org/wikipedia/sh/thumb/a/a0/Kototamopeva.jpg/200px-Kototamopeva.jpg", '10.0',"Dotrajalim autobusom firme Krstic i sin,za Beograd krece grupa putnika.Svako od njih ima neodlozne razloge da sto pre stigne u prestonicu. Prolece je 1941. Dan uoci nemackog napada na Jugoslaviju.",'1');
INSERT INTO `peca`.`cinema_projection` (`name`, `actors`,`geners`, `director`,`runtime`, `poster`, `rating`,`storyline`,`crepertoar`) VALUES ('Zikina dinastija', 'Gidra,M.Todorovic,N.Kojo','komedija','Z.Calic','130',"http://www.alo.rs/resources/images/0000/074/903/jelena%20zigon_1000x0.jpg", '9.9',"Zikina dinastija je sedmi nastavak filmskog serijala Lude Godine, koji je premijerno prikazivanje imao 31.10.1985.godine.",'1');
INSERT INTO `peca`.`cinema_projection` (`name`, `actors`,`geners`, `director`,`runtime`, `poster`, `rating`,`storyline`,`crepertoar`) VALUES ('Balkanski spijun', 'D.Stojkovic','komedija','D.Kovacevic','130',"https://visoko.co.ba/wp-content/uploads/2016/09/Balkanski-%C5%A1pijun-1180x502.jpg", '9.9',"Balkanski spijun je drama o politickoj paranoji Ilije Cvorovića, bivseg staljiniste.Sledeci svoju paranoju, on proganja svog podstanara, povratnika iz Francuske, u kom vidi drzavnog neprijatelja i spijuna.",'2');
INSERT INTO `peca`.`cinema_projection` (`name`, `actors`,`geners`, `director`,`runtime`, `poster`, `rating`,`storyline`,`crepertoar`) VALUES ('Montevideo, Bog te video!', 'M.Bikovic,P.Strugar','drama','D.Bjelogrlic','130',"https://i2.wp.com/www.reprezentacija.rs/wp-content/uploads/montevideo-bog-te-video.jpg", '9.9'," Scenario je napisan po istoimenoj knjizi sportskog novinara Vladimira Stankovica koja govori o velikom uspehu reprezentacije Kraljevine Jugoslavije na Svetskom prvenstvu u fudbalu u Urugvaju 1930.",'2');
INSERT INTO `peca`.`cinema_projection` (`name`, `actors`,`geners`, `director`,`runtime`, `poster`, `rating`,`storyline`,`crepertoar`) VALUES ('Mi nismo andjeli', 'S.Todorovic,N.Kojo','komedija','S.Dragojevic','130',"https://upload.wikimedia.org/wikipedia/sr/b/bb/Mi_nismo_andjeli.jpg", '9.9',"Dvadesetogodisnji student knjizevnosti Nikola ima dva problema: redovnu jutarnju amneziju pracenu mamurlukom i budjenje u krevetima nepoznatih devojaka. Nevolja pocinje kada se u njegov zivot umesaju trudnoca i ljubav.",'3');
INSERT INTO `peca`.`cinema_projection` (`name`, `actors`,`geners`, `director`,`runtime`, `poster`, `rating`,`storyline`,`crepertoar`) VALUES ('Nacionalna klasa', 'D.Nikolic','drama,komedija','G.Markovic','130',"https://ia.media-imdb.com/images/M/MV5BOWNmOWIyYjAtM2NmMi00MGE0LWIyZjgtZGVhZDNkOWRkMWY2XkEyXkFqcGdeQXVyNjcyOTkzNTI@._V1_.jpg", '9.9',"Mladic iz beogradskog predgradja, Flojd, (Dragan Nikolic), automobilski trkac u nacionalnoj klasi,da bi presao u visu klasu, mora da pobedi na vaznoj trci..",'3');
INSERT INTO `peca`.`theatre_projection`(`name`, `actors`,`geners`, `director`,`runtime`, `poster`, `rating`,`storyline`,`trepertoar`) VALUES ('Uobrazeni bolesnik','D.Micanovic','drama','J.Markovic','130',"http://ludus-online.rs/wp-content/uploads/2015/06/Uobra%C5%BEeni-bolesnik-copy.jpg", '8.6',"Molijerova kritika savremene medicinske nauke zasnovana je na uverenju da je nacin na koji ljudska masina funkcionise i dalje misterija. ",'2');
INSERT INTO `peca`.`theatre_projection`(`name`, `actors`,`geners`, `director`,`runtime`, `poster`, `rating`,`storyline`,`trepertoar`) VALUES ('Na Drini cuprija','M.Samolov,J.Maksic','roman','K.Mladenovic','130',"http://www.opanak.net/wp-content/uploads/2015/12/ivo-andric.jpg", '8.6',"Most je u romanu simbol cvrste gradjevine koja je vecno prisutna, dok su ljudski zivoti prolazni.",'1');
INSERT INTO `peca`.`theatre_projection`(`name`, `actors`,`geners`, `director`,`runtime`, `poster`, `rating`,`storyline`,`trepertoar`) VALUES ('Slucajna smrt jednog anarhiste','N.Djuricko','drama','M.Maletkovic','130',"http://jdp.rs/wp-content/uploads/765x300-anarhista.jpg", '8.6',"Ansambl, predvodjen Nikolom Djurickom, raskrinkace sve nelogicnosti vezane za smrt jednog anarhiste, jer iza maske komedije vreba svet organizovanog kriminala, drzavne korupcije, politickih ubistava, verske netrpeljivosti, terorizma.",'2');
INSERT INTO `peca`.`theatre_projection`(`name`, `actors`,`geners`, `director`,`runtime`, `poster`, `rating`,`storyline`,`trepertoar`) VALUES ('Kumovi','D.Petrovic,D.Maksimovic','komedija','D.Kovacevic','130',"http://www.narodnopozoristenis.rs/wp-content/uploads/2018/03/kumovi-96346_600x480.jpg", '8.6',"Kumovi su neobicna prica o coveku koji je ostao bez posla sa kerom iz obliznjeg parka koji takodje nema posao...",'3');
INSERT INTO `peca`.`theatre_projection`(`name`, `actors`,`geners`, `director`,`runtime`, `poster`, `rating`,`storyline`,`trepertoar`) VALUES ('Cigani lete u nebo','I.Bosiljcic,I.Knezevic','mjuzikl','D.Stanojevic','130',"http://www.narodnopozoristenis.rs/wp-content/uploads/2017/12/Cigani-lete-u-nebo-cover.jpg", '8.6',"Mladic koji se zvao Aleksej Maksimovic sin siromasnog radnika,siroce koje je rano ostalo bez oca,imao je 24 godine kada je dospeo u tamnicu..",'3');
INSERT INTO `peca`.`theatre_projection`(`name`, `actors`,`geners`, `director`,`runtime`, `poster`, `rating`,`storyline`,`trepertoar`) VALUES ('Pop Cira i pop Spira','M.Samolov,J.Maksic','roman','S.Sremac','130',"https://static.kupindoslike.com/Pop-Cira-i-pop-Spira-Stevan-Sremac_slika_O_44723089.jpg", '8.6',"Romanticna komedija u kojoj dve popovske kuce zive idilicno,sve dok se u njihovim zivotima ne pojavi mladi ucitelj,u kome obe porodice vide savrsenu priliku za udaju svojih kceri",'1');
